(122, 'Chuyển phát nhanh hỏa tốc', 'chuyen-phat-nhanh-hoa-toc', '', '1457401934.jpg', 'Chuyển phát nhanh hỏa tốc', 'Chuyển phát nhanh hỏa tốc của Hoàng Hà là dịch vụ chuyển phát nhanh có chất lượng cao, thời gian toàn trình được rút ngắn so với dịch vụ chuyển phát nhanh trong nước.', 'chuyển phát nhanh hỏa tốc,chuyen phat nhanh hoa toc', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 1384167300, 'vi', 1, 2, 3, NULL, ',15,19,'),
(123, 'Chuyển phát nhanh hẹn giờ', 'chuyen-phat-nhanh-hen-gio', '', '1457401993.jpg', 'Chuyển phát nhanh hẹn giờ', 'Chuyển phát nhanh hẹn giờ của Hoàng Hà là dịch vụ chuyển phát hỏa tốc có xác định thời gian phát, Quý khách có thể yêu cầu Công ty Hoàng Hà phát bưu gửi trước thời gian công bố của dịch vụ chuyển phát hỏa tốc.', 'chuyển phát nhanh hẹn giờ,chuyen phat nhanh hen gio', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 1383926400, 'vi', 1, 2, 3, NULL, ',15,19,'),
(124, 'Chuyển phát nhanh trong nước', 'chuyen-phat-nhanh-trong-nuoc', '', '1457402063.jpg', 'Chuyển phát nhanh trong nước', 'Công ty Hoàng Hà mang vận đơn của Quý khách đến bất kỳ địa chỉ nào trong lãnh thổ Việt Nam, với thời gian được xác định từ trước theo hình thức door-to-door nhận tận nơi- giao tận nơi', 'chuyển phát nhanh trong nước,chuyen phat nhanh trong nuoc', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 1383924120, 'vi', 1, 2, 3, NULL, ',15,19,'),
(125, 'Chuyển phát nhanh quốc tế', 'chuyen-phat-nhanh-quoc-te', '', '1457402141.jpg', 'Chuyển phát nhanh quốc tế', 'Hoàng Hà cung cấp một giải pháp toàn cầu cho việc chuyển phát nhanh tài liệu và bưu kiện tới tận nhà trên toàn thế giới và trong một loạt các tùy chọn thời gian vận chuyển sẽ đáp ứng mọi nhu cầu của bạn.', 'chuyển phát nhanh quốc tế,chuyen phat nhanh quoc te', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 1380547140, 'vi', 1, 2, 3, NULL, ',15,19,'),
(126, 'Dịch vụ phát theo giờ hẹn trước', 'dich-vu-phat-theo-gio-hen-truoc', '', '1457402358.jpg', 'Dịch vụ phát theo giờ hẹn trước', 'Dịch vụ phát theo giờ hẹn trước là dịch vụ khi Quý khách gửi trong thời gian nhất định thì người nhận của Quý khách sẽ nhận được trước giờ hẹn tại một số nơi.', 'dịch vụ phát theo giờ hẹn trước,dich vu phat theo gio hen truoc', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 1384513500, 'vi', 1, 2, 3, NULL, ',15,20,');
